{
  "openapi": "3.0.1",
  "info": {
    "title": "API de Times e Jogadores",
    "description": "API de Times e Jogadores",
    "version": "1.0.0",
    "contact": {
      "url": "https://github.com/MarcosdeAndrade-byte"
    }
  },
  "servers": [
    {
      "url": "http://localhost:3000"
    }
  ],
  "paths": {
    "/teams/": {
      "post": {
        "tags": [
          "Teams"
        ],
        "summary": "Create a team",
        "description": "Create a new team",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/teams_2"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "OK"
          },
          "400": {
            "description": "Para adicionar um time adicione todos os dados: id,name,coach,stadium,city"
          }
        }
      },
      "get": {
        "tags": [
          "Teams"
        ],
        "summary": "Get all teams",
        "description": "Get all teams",
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "name": {
                      "type": "string"
                    },
                    "coach": {
                      "type": "string"
                    },
                    "stadium": {
                      "type": "string"
                    },
                    "city": {
                      "type": "string"
                    }
                  },
                  "example": {
                    "name": "Flamengo",
                    "coach": "Dorival Júnior",
                    "stadium": "Maracanã",
                    "city": "Rio de Janeiro"
                  }
                }
              }
            }
          },
          "500": {
            "description": "Erro ao tentar listar Times"
          }
        }
      }
    },
    "/teams/id": {
      "get": {
        "tags": [
          "Teams"
        ],
        "summary": "Get teams",
        "description": "Get teams by Id",
        "parameters": [
          {
            "name": "id",
            "in": "header",
            "description": "The teams Id",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/teams"
                }
              }
            }
          },
          "500": {
            "description": "ERROR IN INTERNAL SERVER"
          }
        }
      }
    },
    "/players/": {
      "put": {
        "tags": [
          "Players"
        ],
        "summary": "Create a player",
        "description": "Create a new player",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/player"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "OK"
          },
          "400": {
            "description": "ERROR IN INTERNAL SERVER"
          }
        }
      },
      "get": {
        "tags": [
          "Players"
        ],
        "summary": "Get players by id",
        "description": "Get players by id",
        "parameters": [
          {
            "name": "id",
            "in": "header",
            "description": "The player Id",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/teams"
                }
              }
            }
          },
          "400": {
            "description": "Usuario não encontrado"
          }
        }
      },
      "post": {
        "tags": [
          "Players"
        ],
        "summary": "Remove player",
        "description": "Remove player from team by id",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "string"
                  }
                },
                "example": {
                  "id": "03d8a4b6-f4e3-4f23-afab-35fb8f364740"
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "OK"
          },
          "400": {
            "description": "Usuário não encontrado"
          }
        }
      }
    },
    "/players/goals": {
      "get": {
        "tags": [
          "Players"
        ],
        "summary": "returns player with the most goals",
        "description": "returns player with the most goals",
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/player"
                }
              }
            }
          },
          "500": {
            "description": "Não foi possível retornar o Jogador"
          }
        }
      }
    },
    "/players/sum": {
      "get": {
        "tags": [
          "Players"
        ],
        "summary": "returns the sum of all goals scored",
        "description": "returns the sum of all goals scored",
        "parameters": [
          {
            "name": "team_id",
            "in": "header",
            "description": "The team_id",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "SUM(goals)": {
                      "type": "number"
                    }
                  },
                  "example": {
                    "SUM(goals)": 20
                  }
                }
              }
            }
          },
          "500": {
            "description": "Não foi possível contabilizar o número de gols"
          }
        }
      }
    },
    "/players/positions": {
      "get": {
        "tags": [
          "Players"
        ],
        "summary": "list player by position",
        "description": "list player by position",
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/positions"
                }
              }
            }
          },
          "500": {
            "description": "Não foi possível listar os usuários com base em suas posições"
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "teams": {
        "properties": {
          "id": {
            "type": "string",
            "example": "03d8a4b6-f4e3-4f23-afab-35fb8f364740"
          },
          "name": {
            "type": "string",
            "example": "Flamengo"
          },
          "coach": {
            "type": "string",
            "example": "Dorival Júnior"
          },
          "stadium": {
            "type": "string",
            "example": "Maracanã"
          },
          "city": {
            "type": "string",
            "example": "Rio de Janeiro"
          },
          "players": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/player"
            }
          }
        }
      },
      "teams_2": {
        "properties": {
          "id": {
            "type": "string",
            "example": "03d8a4b6-f4e3-4f23-afab-35fb8f364740"
          },
          "name": {
            "type": "string",
            "example": "Flamengo"
          },
          "coach": {
            "type": "string",
            "example": "Dorival Júnior"
          },
          "stadium": {
            "type": "string",
            "example": "Maracanã"
          },
          "city": {
            "type": "string",
            "example": "Rio de Janeiro"
          }
        }
      },
      "player": {
        "properties": {
          "id": {
            "type": "string",
            "example": "03d8a4b6-f4e3-4f23-afab-35fb8f364740"
          },
          "team_id": {
            "type": "string",
            "example": "03d8a4b6-f4e3-4f23-afab-35fb8f364740"
          },
          "name": {
            "type": "string",
            "example": "Marcos"
          },
          "age": {
            "type": "number",
            "example": 19
          },
          "position": {
            "type": "string",
            "example": "Defenders"
          },
          "goals": {
            "type": "number",
            "example": 29
          }
        }
      },
      "positions": {
        "properties": {
          "Goalkeeper": {
            "type": "array",
            "example": []
          },
          "Defenders": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/player"
            }
          },
          "Midfielder": {
            "type": "array",
            "example": []
          },
          "Forward": {
            "type": "array",
            "example": []
          }
        }
      }
    }
  }
}
